mapper.select:
  	select * from
    ( select a.*, rownum rn from
	    	(SELECT    '('||courtyear||')'||courtshort||courtsn||'号' AS caseno,
                case 
                when CATEGORY = '1'
         			then REPLACE(G_SENCHANGE_AMPLITUDE_BIGNUM(COURTCHANGE), '减为', '')
                when  CATEGORY='7' and LENGTH (COURTCHANGE) = 6 and SUBSTR(COURTCHANGE, 1, 2) > 5
                                then '减为'|| G_SENCHANGE_AMPLITUDE_BIGNUM (COURTCHANGE)
                                else  G_SENCHANGE_AMPLITUDE_BIGNUM(COURTCHANGE)
                    end  AS biandongfudu,
               REPLACE( G_SENCHANGE_AMPLITUDE_BIGNUM(SENTENCE),'减为','') AS bianhouxingqi,
               REPLACE(F_LOSEPOWER_SD (LOSEPOWER),'剥夺政治权利','') as boquan,
                F_TRY_FORMATDATE(COURTSANCTION) as date1,
            	  tsh.courtname||tsh.courttitle as pancaifayuan,
                 case when length(tsh.courtchange) = 4 then F_FORMATYMD((tsh.courtchange),0,0)
                         else
                        F_FORMATYMD(TO_NUMBER(substr(tsh.courtchange,0,2)),TO_NUMBER(substr(tsh.courtchange,3,2)),TO_NUMBER(substr(tsh.courtchange,5,2)),0)
                        end  as commuterange1,
            	tsh.*
       		FROM TBDATA_SENTCHAGE tsh
		    where  tsh.crimid = #{crimid,jdbcType=VARCHAR}

				 order by ${sortField} ${sortOrder}

	  	) a 

	   		where rownum <= #{end}

    )

   		where  rn >= #{start}

select crimid,name,to_char(rewardstart,'yyyyMMdd') as rewardstart, to_char(rewardend,'yyyyMMdd') as rewardend, to_char(courtchangeto,'yyyyMMdd') as courtchangeto, to_char(courtchangefrom,'yyyyMMdd') as courtchangefrom, changetype, to_char(execdate,'yyyyMMdd') as execdate, to_char(courtsanction,'yyyyMMdd') as courtsanction, courtsn,courtshort,courtyear,courtname, (case when courtchangeyear='9995' then '减为无期' else ( (case when courtchangeyear is not null then courtchangeyear||'年' else '' end)|| (case when courtchangemonth is not null then courtchangemonth||'月' else '' end)|| (case when courtchangeday is not null then courtchangeday||'天' else '' end) ) end ) as courtchangeyear,
(case when punishmentyear='9995' then '无期徒刑' else ( (case when punishmentyear is not null then punishmentyear||'年' else '' end)|| (case when punishmentmonth is not null then punishmentmonth||'月' else '' end)|| (case when punishmentday is not null then punishmentday||'天' else '' end) ) end ) as punishmentyear
from TBXF_SENTENCEALTERATION_HIS t where t.crimid = #{crimid,jdbcType=VARCHAR} and to_char(t.courtsanction,'yyyy-MM-dd') =#{courtsanction,jdbcType=VARCHAR}

select count(*)
from ZHUNAN_RCKH
where pr_id1 = #{crimid,jdbcType=VARCHAR}
