mapper.select:
select 

from TBYZ_CHECK_FOR_WORK
where CRIMID = #{crimid,jdbcType=VARCHAR}
  and YEARDATE = #{yeardate,jdbcType=VARCHAR}

mapper.delete:
delete from TBYZ_CHECK_FOR_WORK
where CRIMID = #{crimid,jdbcType=VARCHAR}
  and YEARDATE = #{yeardate,jdbcType=VARCHAR}

mapper.select:
  		SELECT * FROM 
	 	( SELECT A.*, ROWNUM RN 
		 	FROM 
		 	(
		 		SELECT * FROM TBYZ_CHECK_FOR_WORK t

		 		  order by crimid desc
		 	) A 
		 	WHERE ROWNUM <= #{end,jdbcType=DECIMAL}
	 	) 
	 	WHERE RN >= #{start,jdbcType=DECIMAL}

select * from ( select a.*, rownum rn from ( SELECT tbi.crimid, tbi.name, #{yeardate} as yeardate, day01, day02, day03, day04, day05, day06, day07, day08, day09, day10, day11, day12, day13, day14, day15, day16, day17, day18, day19, day20, day21, day22, day23, day24, day25, day26, day27, day28, day29, day30, day31 FROM TBPRISONER_BASEINFO tbi, TBPRISONER_BASE_CRIME tbc, (select * from TBYZ_CHECK_FOR_WORK where YEARDATE = #{yeardate} ) w WHERE tbi.crimid=tbc.crimid and tbi.crimid=w.crimid(+) and tbc.DETAINSTATUS='在押'
and (tbi.crimid like '%'||#{key}||'%' or tbi.name like '%'||#{key}||'%' or tbi.NAMEPINYINSHORT like UPPER('%'||#{key}||'%') )
and exists( select 'x' from tbflow_org_org og where og.orgid=#{orgid} and og.dorgid=tbc.orgid2 ) order by tbi.crimid ) a
where rownum <= #{end}
)
where rn >= #{start}

select * from TBYZ_CHECK_FOR_WORK 
where YEARDATE=#{yeardate,jdbcType=VARCHAR} 
   and crimid=#{crimid}

select count(*) from TBYZ_CHECK_FOR_WORK
 where CRIMID = #{crimid, jdbcType = VARCHAR}
   and YEARDATE = #{yeardate, jdbcType = VARCHAR}
