mapper.select:
select * from TBPRISONER_BASE_CRIME t ,TBYWGK_ACCOUNT m,TBPRISONER_SOCIAL_RELATION n where m.crimid=n.crimid and m.crimid=t.CRIMID
and n.remark=#{remark}
and t.CRIMID = #{crimid} and rownum = 1

select * from ( select rownum rs, t.crimid, t.crimid as applyid, t.name, getage(t.birthday) as age, t.gender, t.registeraddressdetail, a.detainstatus, a.maincase, a.orgname1 as orgname from TBPRISONER_BASEINFO t, tbprisoner_base_crime a where t.crimid = a.crimid
and a.orgid2 in ( select dorgid from tbflow_org_org org where org.orgid=#{orgid})
and a.detainstatus like '%'||#{zaiyacombo}||'%'
and a.orgid1 = #{sdid}
and a.orgid1=#{dep}
and (t.crimid like '%'||#{key}||'%' or t.name like '%'||#{key}||'%' or t.namepinyinshort like '%'||upper(#{key})||'%')
and rownum<=${end}
order by ${sortField} ${sortOrder}
)aa where aa.rs>=${start}

select count(*) from (select t.crimid, t.name, getage(t.birthday) as age, t.gender, t.registeraddressdetail, a.detainstatus, a.maincase, a.orgname1 from TBPRISONER_BASEINFO t, tbprisoner_base_crime a where t.crimid = a.crimid
and a.orgid2 in ( select dorgid from tbflow_org_org org where org.orgid=#{orgid})
and a.orgid1=#{dep}
and a.detainstatus like '%'||#{zaiyacombo}||'%'
and a.orgid1 = #{sdid}
and (t.crimid like '%'||#{key}||'%' or t.name like '%'||#{key}||'%' or t.namepinyinshort like '%'||upper(#{key})||'%')
)

		SELECT *
		  FROM (SELECT s.*, ROWNUM AS rn FROM (SELECT c.crimid,c1.name as criname,c.orgname2,c.arrestauthority,c.judgmentname,
		               '('||c.caseyear||')'||c.caseorgshort||caseno||'号' caseorgshort,c.punishmenttype,c.sentencetype,c.maincase
		          FROM TBPRISONER_BASE_CRIME c,TBPRISONER_BASEINFO c1 WHERE c.crimid = c1.crimid(+) 

                            and (c1.crimid like concat(concat('%', #{key}), '%') or c1.name like concat(concat('%', #{key}), '%'))

                  and (c.orgid1 IN (SELECT dorgid FROM TBFLOW_ORG_ORG o WHERE o.orgid = #{orgid})
                      OR c.orgid2 IN (SELECT dorgid FROM TBFLOW_ORG_ORG o WHERE o.orgid = #{orgid}))

	     	order by ${sortField}  ${sortOrder}

	) s                                

   		where rownum <= #{end}

    )

   		where  rn >= #{start}

select * from ( select * from ( select s.*,rownum as nums from ( select crimid as applyid, flowdraftid as flowdraftid, cbiname as applyname, casemain as maincase, yuanpanxingqi as punishmentyear, to_char(xingqiqiri,'yyyy-MM-dd') as courtchangefrom, to_char(xingqizhiri,'yyyy-MM-dd') as courtchangeto, shengyuxingqi as courtchangeyear, to_char(rujianshijina,'yyyy-MM-dd') as inprisondate, '1' as submitstate, '' as intoo, yuzhengkehefen, yuzhengkekaohe, jifenkaoheqizhi, xingzhengjiangcheng from NX_JXJS_CPB where liucheng IN (SELECT snodeid FROM TBFLOW_DELIVER WHERE flowdefid = 'other_jxjscpb' and state = 0 and departid = #{departid} AND resid IN (SELECT ro.resid FROM TBSYS_USERS_ROLEs us, TBSYS_ROLE_RES ro WHERE us.userid = #{userid} AND us.roleid = ro.roleid)) and curyear||batch = (SELECT curyear || batch FROM ( SELECT * FROM TBXF_COMMUTEPAROLE_BATCH WHERE curyear = (SELECT MAX (curyear) FROM TBXF_COMMUTEPAROLE_BATCH WHERE departid =#{departid}) AND departid =#{departid} ORDER BY batch DESC) WHERE ROWNUM = 1) and ( (orgid IN (SELECT org1.dorgid FROM TBFLOW_ORG_ORG org1 WHERE org1.orgid = #{orgid})) OR (orgid IN (SELECT org2.dorgid FROM TBFLOW_ORG_ORG org2 WHERE org2.orgid = #{orgid})))) s
where applyid like concat(concat('%',#{key}),'%') or applyname like concat(concat('%',#{key}),'%')
) where nums < #{end}) where nums >= #{start}

select * from (select a.*, rownum rn from( SELECT (case when tbc.punishmenteight is not null and tbc.punishmenteight= '1' then '是(已认定)' else case when tbc.judgedate >= to_date('2011/05/01','yyyy/MM/dd') and tbc.crimedate >= to_date('2011/05/01','yyyy/MM/dd') then '是(未认定)' else '否' end end) as punishmenteight, decode(punishmenteight,'','0',null,'0',punishmenteight) teight, to_char(tbc.judgedate,'yyyy"年"MM"月"dd"日"') judgedate, to_char(tbc.crimedate,'yyyy"年"MM"月"dd"日"') crimedate, tbi.crimid, tbi.name, tbc.maincase, tbc.sanremark, to_char(tbc.inprisondate,'yyyy-MM-dd') inprisondate, tbc.whereto, tbc.payment, tbc.compensation, tbc.stolenmoney, tbc.returnloot, tbc.fulfilcompensation, tbc.expropriation, tbc.forfeit, tbc.forfeitureproperty, tbc.CASENATURE importantstatus, tbc.caseothertype, (CASE WHEN tbc.orgname2 is not null THEN tbc.orgname2 ELSE tbc.orgname1 END) AS orgname, (case when decode(tbc.orgid2,null,'','', '','0','',tbc.orgid2)!='' then tbc.orgid2 else tbc.orgid1 end) AS orgid,
DECODE (tbc.undermine, 1, '金融犯罪 ', '')|| DECODE (tbc.postcrime, 1, '职务犯罪 ', '')|| DECODE (tbc.underworld, 1, '黑社会性质组织犯罪 ', '') AS underworld, DECODE (tbc.undermine, 1, '金融犯罪 ', '')|| DECODE (tbc.postcrime, 1, '职务犯罪 ', '')|| DECODE (tbc.underworld, 1, '黑社会性质组织犯罪 ', '') AS underworld2, decode(tbc.sanclassstatus,'0','','','',tbc.sanclassstatus) as sanclassstatus,
tbi.pprovince, tbi.duty
from TBPRISONER_BASEINFO tbi, TBPRISONER_BASE_CRIME tbc where tbi.crimid=tbc.crimid and (tbc.detainstatus ='在押' or tbc.detainstatus ='1' ) and tbc.ORGID2 IN (SELECT a.dorgid FROM TBFLOW_ORG_ORG a where a.orgid = #{orgid})
and (${fzsj})
and (${pjsj})
and ( tbi.crimid like concat(concat('%', #{key}), '%') or tbi.name like concat(concat('%', #{key}), '%') or tbi.namepinyinshort like concat(concat('%', UPPER(#{key}) ), '%') )
order by tbi.${sortField} ${sortOrder}
) a
where rownum <= #{end}
)
where rn >= #{start}
