mapper.select:
select 

from TBSYS_FORM_PARAM_MAPPING
where SOLUTIONID = #{solutionid,jdbcType=VARCHAR}
  and DETAILID = #{detailid,jdbcType=VARCHAR}
  and SQLGROUPID = #{sqlgroupid,jdbcType=VARCHAR}
  and PARAMID = #{paramid,jdbcType=VARCHAR}

	select * from
    ( 
	    select a.*, rownum rn from
	    (
	    	select 

		    from TBSYS_FORM_PARAM_MAPPING  where delflag=0

		   		 and  solutionid=#{solutionid}


		   		 and  detailid=#{detailid}


		   		 and  sqlgroupid=#{sqlgroupid}


		   		 and  paramid=#{paramid}


		        and sqlparamname like '%'||#{sqlparamname}||'%' 


		        and formfieldname like '%'||#{formfieldname}||'%' 


		   		 and datatype=#{datatype}



		     	order by ${sortField}  ${sortOrder}

	  	) a 

	   		where rownum <= #{end}

    )

   		where  rn >= #{start}


